    @GetMapping("/post/{id}")
    public String postPage(@PathVariable String id, Model model){

        BlogPost selectedPost = null;
        for(BlogPost bP : blogPosts){
            if (bP.getId().equalsIgnoreCase(id)){
                selectedPost = bP;
                break;
            }
        }
        if(selectedPost == null){
            System.out.println("No match found, creating default object");
            selectedPost = new BlogPost("N/A", "Failed to find","X", "This content is not available");
        }

        model.addAttribute("blogPost", selectedPost);

        return "blogpost";
    }
